import styled from 'styled-components'

export const Container = styled.div`
    width: 100%;
    height: 100%;
`

export const Header = styled.div`
    max-width: 500px;
    height: 150px;
    background-color: #eaeaea;
    color: black;
    font-size: 25px; 
    display: flex;
    justify-content: center;
    align-items: center;
    font-weight: bold;
    letter-spacing: 2px;
    text-transfrom: uppercase;
`

export const Product = styled.div`
    max-width: 500px;
    height: 250px;
    display: flex;
`

export const ProductList = styled.div`
    flex: 1 1 0px;
    height: 200px;
    border: 1px solid black;
`

export const ProductName = styled.h3`
    letter-spacing: 4px;
`

export const ProductDescription = styled.path`
    font-weight: 500;
`

export const ProductPrice = styled.path`
    font-weight: 800;
`
