import { Container ,Header, Product, ProductList, ProductName, ProductDescription, ProductPrice, ChatInput, ChatSubmit, Chat, ChatInputWrapper, ChatBox, BubbleBot, BubbleUser } from './styled'
import data from './data.json'
import {useState, useEffect} from 'react'

function App() {
  let dataCopy = {...data};
  const [getBot, setBot] = useState([])
  const [getListChat, setListChat] = useState([])
useEffect(() => {
  let datas = [];
  const dataEnable = dataCopy.bot.filter((item) => item.enable)
  console.log(dataEnable);


  dataCopy.bot.map((res, i) => {
    datas.push(res.enable)
  })
  setListChat(datas)
}, [])

  // console.log(dataCopy.bot);

  useEffect(() => {
    console.log("wow ada perubahan data", getListChat);
  }, [getListChat])

  // console.log(getBot);
  return (
    <Container>
      <Header>Toko Cuy</Header>
      {/* Product */}
      <Product>
      <ProductList>
        <ProductName>T-sirt</ProductName>
        <ProductDescription>Lorem Ipsum Doler Ipsum</ProductDescription>
        <ProductPrice>Rp.300.000</ProductPrice>
        </ProductList>
      <ProductList>
        <ProductName>Meja</ProductName>
        <ProductDescription>Lorem Ipsum Doler Ipsum </ProductDescription>
        <ProductPrice>Rp.500.000</ProductPrice>
        </ProductList>
      <ProductList>
        <ProductName>Makanan</ProductName>
        <ProductDescription>Lorem Ipsum Doler Ipsum</ProductDescription>
        <ProductPrice>Rp.100.000</ProductPrice>
        </ProductList>
      </Product>

      <Chat>
        <ChatInputWrapper>
          <ChatBox>
            <BubbleBot>ChatBot</BubbleBot>
            <BubbleUser>ChatBot</BubbleUser>
            <BubbleBot>ChatBot</BubbleBot>
            <BubbleUser>ChatBot</BubbleUser>
            <BubbleBot>ChatBot</BubbleBot>
            <BubbleUser>ChatBot</BubbleUser>
          </ChatBox>
        <ChatInput></ChatInput>
        <ChatSubmit>Submit</ChatSubmit>
        </ChatInputWrapper>
      </Chat>
    </Container>
  );
}

export default App;
